export const dialogIcons: RR<
  keyof typeof dialogIconTriggers,
  JSX.Element | undefined
> = {
  none: undefined,
  error: <span className="text-red-500">{icons.exclamationCircle}</span>,
  warning: (
    <span className="text-brand-200 dark:text-brand-400">
      {icons.exclamation}
    </span>
  ),
  success: <span className="text-green-500">{icons.checkCircle}</span>,
  info: icons.informationCircle,
};

export type IconProps = {
  // Require title attribute
  readonly title: string;
  // Require passing one of the defined icons
  readonly icon: Icon;
  // Don't allow passing children
  readonly children?: undefined;
};

export type Icon = keyof typeof icons;
